// Generated by CoffeeScript 1.6.3


$(document).ready(function() {
  $("[nav-toggle]").hover(function() {
    return $(this).fadeTo(0, 0, function() {
      return $(this).fadeTo(500, 1);
    });
  });
  $("[nav-toggle]").click(function() {
    var href;
    href = $(this).attr("nav-toggle");
    return $('#mainContent').fadeOut("slow", function() {
      $.ajax({
        type: 'GET',
        url: '/welcome/ajaxNav/'+href,
        dataType: 'script'

      });
      return $("#mainContent").fadeIn("slow");
    });
  });
  $(".icon").mouseenter(function() {
    return $(this).animate({
      opacity: .01
    });
  });
  return $(".icon").mouseleave(function() {
    return $(this).animate({
      opacity: 1
    });
  });
});
